
public class AirthmaticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int firstNumber = 5;
		int secondNumber = firstNumber++;
		int addedNumber = firstNumber + secondNumber;
		System.out.println("Added value of first two numbers is "+ addedNumber);
		int dividedNumber = addedNumber / firstNumber;
		System.out.print("Remainder of divided value is " + dividedNumber);
		
	}

}
